                    icon: 'success',
                    text: this.forget_password_res.message + " " + email
                });
            }
            if (this.forget_password_res.status == 404) {
                Swal.fire({
                    icon: 'error',
                    text: this.forget_password_res.message + " " + email
                });
            }
        });
    }
}
